// 随机获取数字元素
function randomItem(arr) {
    return  arr[ Math.floor( Math.random() * arr.length ) ];
}

// 返回 0 到i的 随机整数
function randomInt(i=10) {
    return Math.floor(Math.random() * i);
}


// 随机返回多个元素
function randomItems(arr, num=1) {
    var len = arr.length;
    var res = [];
    for(var i = 0; i<num; i++) {
        res.push(arr[Math.floor(Math.random() * len)]);
    }
    return res;
}

// js触发，先输入同一个空格，让然后再删除, 解决 表单控件双向绑定会忽略js对value的改变 
function inputByJs(el) {
    var backSpaceCode  = 8;
    var spaceCode = 32;
    var evtObj = document.createEvent('UIEvents'); 
    evtObj.initUIEvent( 'keydown', true, true, window, 1 );

    delete evtObj.keyCode;
    Object.defineProperty(evtObj,"keyCode",{value:spaceCode});
    el.dispatchEvent(evtObj);  // 按下空格键

    delete evtObj.keyCode;
    Object.defineProperty(evtObj,"keyCode",{value:backSpaceCode});
    el.dispatchEvent(evtObj); // 按下回格键

}

