// 获取随机的日期
function getRandomDate() {
    var head = ["19","2","3"];
    var MonthArr = ["01","02","03","04","05","06","07","08","09","10","11","12"];
    var DayArr   = [31,28,31,30,31,30,31,31,30,31,30,31];
    head = randomItem(head);
    if( head == "19") {
        year = head + randomInt() + randomInt();
    } else {
        year = head + randomInt() + randomInt() + randomInt();
    }
    month = randomItem(MonthArr);
    day = randomInt(DayArr[parseInt(month)-1]) + 1;
    day = day < 10 ? "0"+day : ""+day;
    return  year+month+day;
}

